import React from 'react';
import logo from './logo.svg';
import './app.css';
import data from './Util/sample.json';
class App extends React.Component {
  togglePressed = () => {
    var x = document.getElementById("switch");
    x.style.height = "100%";
  }

  componentDidMount() {
    {
      data.entries.map((item, index) => {
        console.log('data === ', item)
      })
    }
  }

  render(){
    return (
      <div className="app">
        <div className = "header-container">
          <span className = "header-subtitle">DEMO Streaming</span>
        </div>
        <div className = "sub-header-container">
          <span className = "sub-header-subtitle">Popular Titles</span>
        </div>
        <div className = "body-container">
          <div className = "body-wrapper">
            <div className = "selection-box-container">
              <div className = "selection-box">
                  <span className = "selection-box-title">Series</span>
              </div>
              <div>
                <span className = "selection-box-subtitle">Popular Series</span>
              </div>
            </div>
            <div className = "selection-box-container">
              <div className = "selection-box">
                  <span className = "selection-box-title">Movies</span>
              </div>
              <div>
                <span className = "selection-box-subtitle">Popular Movies</span>
              </div>
            </div>
          </div>
        </div>
        <div className = "footer-container" >
          <div className = "footer-inner-container">
            <div className = "footer-pages-info">
              <p class="footer-links">
                <a href="#">Home</a>
                |
                <a href="#">Term And Condition</a>
                |
                <a href="#">Privacy Policy</a>
                |
                <a href="#">Collection Statement</a>
                |
                <a href="#">Help</a>
                |
                <a href="#">Manage Account</a>
              </p>
            </div>
            <div className = "footer-copyright">
              <p class="copyright-text">Copyright &copy; 2017 All Rights Reserved by 
                <a href="#">Scanfcode</a>.
              </p>
            </div>
            <div className = "footer-social-container">
              <div className = "social-login">

              </div>
              <div className = "social-download">

              </div>
            </div>
          </div>
        </div>
        {/* <div className = "movies-container">
          {
            data.entries.map((item, index) => {
              return(
                <div className = "movies-list">
                  <img src={item.images['Poster Art'].url} className = "movie-poster" />
                  <span className = "movies-title">{item.title}</span>
                </div>
              )
            })
          }
        </div> */}
      </div>
    );
  }
}

export default App;
