import React from 'react';
import '../app.css';
import data from '../Util/sample.json';
import Header from '../Components/header'
import SubHeader from "../Components/subHeader"
import SelectionBox from "../Components/selectionBox"
import Footer from "../Components/footer"
import Layout from "./layout"
import ShowStream from "./series"
class Home extends React.Component {
  state = {
      showMovies : false,
      showSeries : false,
      showSelection : true,
      moviesData : [],
      seriesData : [],
      isLoading : false,
  }

  componentDidMount() {
    {
      data.entries.map((item, index) => {
        //console.log('data === ', item)
      })
    }
    // fetch('../Util/sample.json').then(response => {
    //     console.log('response === ', response.json())
    //     return response.json();
    //   }).then(data => {
    //     console.log(data);
    //   }).catch(err => {
    //   });

  }



  onSeriesPressed = async () => {
    this.setState({isLoading : true})
    {
        await data.entries.map((item, index) => {
          //console.log('data === ', item)
          if(item.programType === "series")  {
              this.setState({
                seriesData : item,
                showMovies : false,
                showSeries : true,
                showSelection : false,
                isLoading : false
              }, () => {
                  console.log('here == ', this.state.seriesData)
              })
          }
        })
    }
  }

  onMoviesPressed = async () => {
    this.setState({isLoading : true})
    {
        data.entries.map((item, index) => {
          console.log('data === ', item)
          if(item.programType === "movie")  {
              this.setState({
                moviesData : item,
                showMovies : true,
                showSeries : false,
                showSelection : false,
                isLoading : false
              })
          }
        })
    }
}

  render(){
      const { isLoading, showMovies, showSeries, showSelection, seriesData, moviesData } = this.state;
    return (
      <Layout>
        <SubHeader title = {'Popular Streaming'}/>
        {
            isLoading &&
            <div>
                <p>Loading...</p>
            </div>
        }
        {
            showSelection && !isLoading &&
            <div className = "body-container">
                <div className = "body-wrapper">
                    <SelectionBox 
                    title = {'Series'}
                    subtitle = {'Popular Series'}
                    onPress = {this.onSeriesPressed}
                    />
                    <SelectionBox 
                    title = {'Movies'}
                    subtitle = {'Popular Movies'}
                    onPress = {this.onMoviesPressed}
                    />
                </div>
            </div>
        }
        {
            showSeries && !isLoading && 
            <ShowStream data = {seriesData} />
        }
        {
            showMovies && !isLoading && 
            <ShowStream data = {data} />
        }
      </Layout>
    );
  }
}

export default Home;
