import React from 'react';
import '../app.css';
import data from '../Util/sample.json';
import Header from '../Components/header'
import SubHeader from "../Components/subHeader"
import SelectionBox from "../Components/selectionBox"
import Footer from "../Components/footer"
import Layout from "./layout"
import ShowStream from "./series"
class Home extends React.Component {
  state = {
      showMovies : false,
      showSeries : false,
      showSelection : true,
      moviesData : [],
      seriesData : []
  }

  componentDidMount() {
    // {
    //   data.entries.map((item, index) => {
    //     console.log('data === ', item)
    //   })
    // }
    fetch('../Util/sample.json').then(response => {
        return response.json();
      }).then(data => {
        // Work with JSON data here
        console.log(data);
      }).catch(err => {
        // Do something for an error here
      });

  }

  onSeriesPressed = () => {
      this.setState ({
          showMovies : false,
          showSeries : true,
          showSelection : false
      })
  }

  onMoviesPressed = () => {
    this.setState ({
        showMovies : true,
        showSeries : false,
        showSelection : false
    })
}

  render(){
      const { showMovies, showSeries, showSelection } = this.state;
    return (
      <Layout>
        <SubHeader title = {'Popular Streaming'}/>
        {
            showSelection &&
            <div className = "body-container">
            <div className = "body-wrapper">
                <SelectionBox 
                title = {'Series'}
                subtitle = {'Popular Series'}
                onPress = {this.onSeriesPressed}
                />
                <SelectionBox 
                title = {'Movies'}
                subtitle = {'Popular Movies'}
                onPress = {this.onMoviesPressed}
                />
            </div>
            </div>
        }
        {
            showSeries && 
            <ShowStream data = {data} />
        }
        {
            showMovies && 
            <ShowStream data = {data} />
        }
      </Layout>
    );
  }
}

export default Home;
