import React from 'react';
import logo from './logo.svg';
import './app.css';
import data from './Util/sample.json';
class App extends React.Component {
  togglePressed = () => {
    var x = document.getElementById("switch");
    x.style.height = "100%";
  }

  componentDidMount() {
    {
      data.entries.map((item, index) => {
        console.log('data === ', item.images['Poster Art'].url)
      })
    }
  }

  render(){
    return (
      <div className="app">
        {
          data.entries.map((item, index) => {
            return(
              <img src={item.images['Poster Art'].url} className='card-img-top' />
            )
          })
        }
      </div>
    );
  }
}

export default App;
